:root {
    --note: 'Dark-Instagram v2.5.0';
    --bshadow: 0 2px 4px var(--shadow);
    --t: transparent !important;
    --avatar: a_radius;
    --ui-font: "font_name", -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, Ubuntu, Cantarell, 'Noto Sans', var(--emoji-font), sans-serif;
    --emoji-font: "font_name", 'joypixels', 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol', 'Noto Color Emoji';
    --white: #fff;
    --dark: #1f232a;
    --darken: #252a33;
    --darker: #333943;
    --light: #e9e9e9;
    --lighter: #a1a1a1;
    --accent: #7289da;
    --shadow: rgba(0,0,0,0.145);
    --yellow: #e5c512;
    --orange: #df4b16;
    --red: #dc322f;
    --magenta: #f33682;
    --violet: #6c71c4;
    --blue: #268bd2;
