  }
  .monaco-editor .inline-added-margin-view-zone,
  .monaco-editor .char-insert {
    background-color: rgba(0,255,0,0.314) !important;
  }
  .monaco-editor .line-delete {
    color: var(--white) !important;
    border-color: var(--shadow) !important;
    background-color: rgba(117,0,0,0.65) !important;
  }
  .monaco-editor .line-insert {
    color: var(--white) !important;
    border-color: var(--shadow) !important;
    background-color: rgba(79,88,0,0.75) !important;
  }
  .monaco-editor .current-line-margin {
    border-color: var(--darker) !important;
    background-color: var(--darker) !important;
  }
  .hover-overlay hr {
    background-color: var(--darker) !important;
  }
  .hover-overlay__contents {
    border-color: var(--darker) !important;
  }
  .hover-overlay--gitlab .hljs-comment,
  .hover-overlay--gitlab .hljs-quote {
    color: var(--lighter) !important;
  }
  .hover-overlay--gitlab .hljs-section,
  .hover-overlay--gitlab .hljs-selector-id,
  .hover-overlay--gitlab .hljs-title {
    color: var(--light) !important;
  }
  .hover-overlay .loading-spinner {
    border-color: var(--darker) !important;
    border-top-color: var(--accent) !important;
  }
  line[id *= 'dag-'][stroke = '#f2f2f2'] {
    stroke: #808080 !important;
  }
  [data-page='users:show'] .user-profile .cover-block {
    padding-top: 80px;
  }
  [data-page='users:show'] .user-profile .cover-block .cover-controls {
    top: 80px;
  }
  [data-page='users:show'] .user-profile .cover-block::before {
    padding: 16px;
    z-index: 999;
    content: 'v1.0 is finally here! 🎉 🥳 I published an update about my userstyles a while ago, you can read it here: https://vednoc.me/blog/the-end-of-an-era';
    position: absolute;
    top: 0;
    right: 0;
    left: 0;
    min-height: 52px;
    max-height: 208px;
    text-align: center;
    color: var(--light) !important;
    background-color: var(--darker) !important;
  }

.wrapper {
    padding: 1rem;
    min-height: calc(100vh - 2rem);
  }
  .wrapper h1,
  .wrapper p {
    color: var(--light) !important;
  }
  .wrapper a {
    color: var(--accent) !important;
  }
  .wrapper * {
    font-family: var(--ui-font);
  }
  .wrapper .bg {
    filter: grayscale(1) invert(0.5) opacity(0.2);
  }
  .wrapper [class *= 'box'] {
    background-color: var(--dark) !important;
    border-radius: 4px;
    box-shadow: inset 0 0 0 1px var(--darker), var(--bshadow);
  }
  .wrapper [class *= 'box'] .text-switch {
    color: var(--lighter) !important;
  }
  .wrapper [class *= 'box'] [class *= 'toggle'] + label {
    background-color: var(--darker) !important;
  }
  .wrapper [class *= 'box'] [class *= 'toggle'] + label::after {
    background-color: var(--darker) !important;
  }
  .wrapper [class *= 'box'] [class *= 'toggle'] + label::before {
    background-color: var(--dark) !important;
  }
  .wrapper [class *= 'box'] [class *= 'toggle']:checked + label::before {
    background-color: var(--blue) !important;
  }

sourceCode:
/* ==UserStyle==
@name         Material Dark GitHub
@version      3.2.15
@description  A Material Dark theme for GitHub
@namespace    CharlieEtienne
@author       CharlieEtienne
@homepageURL  https://github.com/CharlieEtienne/material-github
@supportURL   https://github.com/CharlieEtienne/material-github/issues
@updateURL    https://raw.githubusercontent.com/CharlieEtienne/material-github/master/material-github.user.css
@license      MIT
@advanced dropdown theme "Theme" {
  oceanic "Oceanic" 
<<<EOT 200, 19% EOT;
  darker "Darker" 
<<<EOT 0, 0% EOT;
  palenight "Palenight" 
<<<EOT 229, 20% EOT;
  custom "Custom Theme"
<<<EOT var(--hue, none), var(--saturation, none) EOT;
}
@var range hue "Custom theme hue" [0, 0, 360, 1, ""]
@var range saturation "Custom theme saturation" [100, "%"]
@var range lightness "Custom theme Lightness" [1, 0.1, 1.9, 0.1, ""]
@advanced dropdown accent "Accent" {
	turquoise "Turquoise" <<<EOT 174 EOT;
	tomato "Tomato" <<<EOT 4 EOT;
	lavender "Lavender" <<<EOT 262 EOT;
	daisy "Daisy" <<<EOT 54 EOT;
	lime "Lime" <<<EOT 89 EOT;
	amethyst "Amethyst" <<<EOT 291 EOT;
	abyss "Abyss" <<<EOT 229 EOT;
	orange "Orange" <<<EOT 15 EOT;
	oceanic "Oceanic" <<<EOT 199 EOT;
	acid_lime "Acid lime" <<<EOT 73 EOT;
	dodgerblue "Dodgerblue" <<<EOT 218 EOT;
	strawberry "Strawberry" <<<EOT 340 EOT;
	light "Light" <<<EOT 0 EOT;
	indigo "Indigo" <<<EOT 231 EOT;
	water "Water" <<<EOT 207 EOT;
	sky "Sky" <<<EOT 180 EOT;
	plant "Plant" <<<EOT 123 EOT;
	aquamarine "Aquamarine" <<<EOT 166 EOT;
	cyan "Cyan" <<<EOT 187 EOT;
	palenight "Palenight" <<<EOT 231 EOT;
