:root{

    --hue:0;
--saturation: 0%;
--custom_accent: hsl(174, 38%, 66%);
	
    --bg1: hsl(var(--hue), var(--saturation), 13%);
    --bg2: hsl(var(--hue), var(--saturation), 15%);
    --bg3: hsl(var(--hue), var(--saturation), 18%);
    --bg4: hsl(var(--hue), var(--saturation), 21%);
    --bg5: hsl(var(--hue), var(--saturation), 24%);
    --bg6: hsl(var(--hue), var(--saturation), 27%);
	
    --ov: hsla(var(--hue), var(--saturation), 70%, .18);
}

.__fb-dark-mode:root, .__fb-dark-mode {
	--web-wash: var(--bg2);
	--wash: var(--bg6);
	--surface-background: var(--bg3);
	--nav-bar-background: var(--bg3);
	--card-background: var(--bg3);
	--comment-background: var(--bg5);
	--popover-background: var(--bg5);
	--secondary-button-background: var(--bg5);
	--hover-overlay: var(--ov);
	--divider: var(--bg5);
	--secondary-button-background-floating: var(--bg6);
	--messenger-card-background: var(--bg3);
	--hosted-view-selected-state: var(--bg4);
	--primary-deemphasized-button-background: var(--bg6);
	--primary-button-background: var(--accent);
 	/*--accent: var(--custom_accent); */
}

