    /* ACCENT VARIATIONS */
    .accent-generator-mixin(@c) {
        @tmp900: '@{c}900';
        @tmp700: '@{c}700';
        @tmp200: '@{c}200';
        @tmp100: '@{c}100';
        @tmpB1 : '@{c}B1';
        @tmpB2 : '@{c}B2';
        @tmpF1 : '@{c}F1';
        @tmpF2 : '@{c}F2';

        @accent   : @@c;
        @accent900: @@tmp900;
        @accent700: @@tmp700;
        @accent200: @@tmp200;
        @accent100: @@tmp100;
        @accentB1 : @@tmpB1;
        @accentB2 : @@tmpB2;
        @accentF1 : @@tmpF1;
        @accentF2 : @@tmpF2;
    }

    .accent-generator-mixin(@accent_color_mode);

    /* LINK COLOR */
    /*link color: used to be #758bff;*/
    .link-generator-mixin(theme) {
        //@link: @linkThemeColor;
        //@link: @accent;
        @link: if((@theme=dark), tint(@accent, 10%), shade(@accent, 10%));
    }

    .link-generator-mixin(custom) {
        @link: @link_color;
